import React, {useState} from 'react';
import { GoogleLogin, GoogleLogout } from 'react-google-login';
import './App.css';
import { BrowserRouter as Router, Switch, 
  Route, Redirect,} from "react-router-dom";
import Profilepage from './Profilepage';


export default function Login() {

    const clientId = "754018186930-p4a5rumvi4su0bdsv6old3rsb5pb6g3v.apps.googleusercontent.com";
    const [showLoginButton, setShowLoginButton] = useState(true);
    const [ showLogoutButton, setShowLogoutButton] = useState(false);
    const onLoginSuccess = (res) => {
    console.log('Login Success',res.profileObj);
    setShowLoginButton(false);
    setShowLogoutButton(true);
    <Router>
        <Switch>
          {/* This route is for home component 
          with exact path "/", in component props 
          we passes the imported component*/}
          <Route exact path="/" component={Profilepage} />
            
          {/* This route is for about component 
          with exact path "/about", in component 
          props we passes the imported component*/}
          <Route path="/Profilepage" component={Profilepage} />
           
          {/* If any route mismatches the upper 
          route endpoints then, redirect triggers 
          and redirects app to home component with to="/" */}
          <Redirect to="/" />
        </Switch>
      </Router>
    }

    const onFailureSuccess=(res) => {
    console.log('Login Failed:',res);
    }

    const onSignoutSuccess = () => {
    alert("You have been signed out successfully");
    setShowLoginButton(true);
    setShowLogoutButton(false);
    console.clear();
    }



  return (
    <div className='signupBig'>

        {showLoginButton ?
        <GoogleLogin
            clientId={clientId}
            render={renderProps => (
            <button id="signupBig" onClick={renderProps.onClick} disabled={renderProps.disabled}>Sign up</button>
            )}
            buttonText="Login"
            onSuccess={onLoginSuccess} 
            onFailure={onFailureSuccess}
            cookiePolicy={'single_host_origin'}
        /> :null
        }

        {showLogoutButton ?
        <GoogleLogout
                clientId={clientId}
                buttonText="Logout"
                onLogoutSuccess={onSignoutSuccess}
            >
        </GoogleLogout> : null
        }


    </div>
  )
}
